function searchBtn() {

    let brandName = document.getElementById('brand-name').value.toLowerCase().replace(/ /g, "")
    let modelName = document.getElementById('model-name').value.toLowerCase().replace(/ /g, "")
    let searhedPhone = document.getElementById('searhed-phone')

    // generating searched cards 
    for (let mobile in mobiles) {
        let keysArray = Object.keys(mobiles[mobile])
        keysArray.map((key) => {
            if (key === modelName && brandName !== '') {
                // console.log(key)
                // console.log(modelName)
                for (let specs in mobiles[mobile]) {
                    // console.log(mobiles[mobile][modelName].model)
                    searhedPhone.innerHTML = `<div class="card">
                            <img src="${mobiles[mobile][modelName].src}" style="width:100%">
                            <h1> ${mobiles[mobile][modelName].model} </h1>
                            <p class="price"> Rs: ${mobiles[mobile][modelName].price}</p>
                            <p> Color: ${mobiles[mobile][modelName].color} </p>
                            <p> Battery: ${mobiles[mobile][modelName].battery} </p>
                            <p><button>Buy Now</button></p>
                        </div>`
                }
            }
        })
    }

    if (mobiles[brandName] !== undefined && mobiles[brandName][modelName] !== undefined) {
        // console.log(mobiles[brandName][modelName])
    }
    else if (brandName == '' || modelName == '') {
        alert('Brand name OR Model name is empty')
    }
    else {
        alert('This model is not available')
    }

}


// generating all cards 
function generateCard() {
    let card;
    for (let mobile in mobiles) {
        // console.log(mobiles[mobile])
        for (let specs in mobiles[mobile]) {
            // console.log(mobiles[mobile][specs])
            let card = `<div class="card">
    <img src="${mobiles[mobile][specs].src}" style="width:100%">
    <h1> ${mobiles[mobile][specs].model} </h1>
    <p class="price"> Rs: ${mobiles[mobile][specs].price}</p>
    <p> Color: ${mobiles[mobile][specs].color} </p>
    <p> Battery: ${mobiles[mobile][specs].battery} </p>
    <p><button>Buy Now</button></p>
</div>`
            document.getElementById("container").innerHTML += card
        }

    }
}
generateCard()



